<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\UserAcceptedTermnsOfUse\Domain\ValueObject\UserAcceptedTermnsOfUseUserVO;
use Civi\Lughauth\Features\Access\Master\User\Domain\UserRef;

final class UserAcceptedTermnsOfUseUserVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new UserRef('one');
        $ref = UserAcceptedTermnsOfUseUserVO::from($value);
        $this->assertEquals(new UserRef('one'), $ref->value());
        $other = UserAcceptedTermnsOfUseUserVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = UserAcceptedTermnsOfUseUserVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = UserAcceptedTermnsOfUseUserVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
