<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingParty;

final class RelyingPartyUnitTest extends TestCase
{
    public function test_init_store_values(): void
    {
        // @Arrange
        $one = new RelyingParty(
            uid: 'one',
            code: 'one',
            apiKey: 'one',
            enabled: true,
            scopes: 'one',
            schemas: 'one',
            policies: 'one',
            version: 1,
        );

        // @Act
        $other = $one->toAttributes()->build();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged());
        $this->assertEquals($one->getApiKey(), $other->getApiKey());
        $this->assertTrue($one->isApiKeyChanged());
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_replace_change_values(): void
    {
        // @Arrange
        $base = new RelyingParty(
            uid: 'one',
            code: 'one',
            apiKey: 'one',
            enabled: true,
            scopes: 'one',
            schemas: 'one',
            policies: 'one',
            version: 1,
        );
        $other = new RelyingParty(
            uid: 'other',
            code: 'other',
            apiKey: 'other',
            enabled: false,
            scopes: 'other',
            schemas: 'other',
            policies: 'other',
            version: 2,
        );

        // @Act
        $one = $base->replace($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged($base));
        $this->assertEquals($one->getApiKey(), $other->getApiKey());
        $this->assertTrue($one->isApiKeyChanged($base));
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
    }
    public function test_create_store_values(): void
    {
        // @Arrange
        $one = new RelyingParty(
            uid: 'one',
            code: 'one',
            apiKey: 'one',
            enabled: true,
            scopes: 'one',
            schemas: 'one',
            policies: 'one',
            version: 1,
        );

        // @Act
        $other = RelyingParty::create($one->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged());
        $this->assertEquals($one->getApiKey(), $other->getApiKey());
        $this->assertTrue($one->isApiKeyChanged());
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
        $this->assertCount(0, $one->getTheEvents());
        $this->assertCount(1, $other->getTheEvents());
    }
    public function test_update_change_values(): void
    {
        $base = new RelyingParty(
            uid: 'one',
            code: 'one',
            apiKey: 'one',
            enabled: true,
            scopes: 'one',
            schemas: 'one',
            policies: 'one',
            version: 1,
        );
        $other = new RelyingParty(
            uid: 'other',
            code: 'other',
            apiKey: 'other',
            enabled: false,
            scopes: 'other',
            schemas: 'other',
            policies: 'other',
            version: 2,
        );

        // @Act
        $one = $base->update($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged($base));
        $this->assertEquals($one->getApiKey(), $other->getApiKey());
        $this->assertTrue($one->isApiKeyChanged($base));
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
        $this->assertCount(0, $other->getTheEvents());
        $this->assertCount(1, $one->getTheEvents());
    }
    public function test_delete_store_values(): void
    {
        // @Arrange
        $one = new RelyingParty(
            uid: 'one',
            code: 'one',
            apiKey: 'one',
            enabled: true,
            scopes: 'one',
            schemas: 'one',
            policies: 'one',
            version: 1,
        );

        // @Act
        $other = $one->delete();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged());
        $this->assertEquals($one->getApiKey(), $other->getApiKey());
        $this->assertTrue($one->isApiKeyChanged());
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_enable_modify(): void
    {
        // @Arrange
        $source = new RelyingParty(
            uid: 'one',
            code: 'one',
            apiKey: 'one',
            enabled: true,
            scopes: 'one',
            schemas: 'one',
            policies: 'one',
            version: 1,
        );
        $sourceEnabled = false;
        $targetEnabled = true;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->enable();

        // @Assert
        $this->assertEquals(false, $source->getEnabled());
        $this->assertEquals($targetEnabled, $target->getEnabled());
        $this->assertNotEquals($sourceEnabled, $target->getEnabled());
    }
    public function test_disable_modify(): void
    {
        // @Arrange
        $source = new RelyingParty(
            uid: 'one',
            code: 'one',
            apiKey: 'one',
            enabled: true,
            scopes: 'one',
            schemas: 'one',
            policies: 'one',
            version: 1,
        );
        $sourceEnabled = true;
        $targetEnabled = false;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->disable();

        // @Assert
        $this->assertEquals(true, $source->getEnabled());
        $this->assertEquals($targetEnabled, $target->getEnabled());
        $this->assertNotEquals($sourceEnabled, $target->getEnabled());
    }
}
