<?php

# @autogenerated
declare(strict_types=1);

use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleNameVO;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleTenantVO;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleVersionVO;
use Civi\Lughauth\Features\Access\Role\Application\Usecase\Create\RoleCreateParams;
use PHPUnit\Framework\TestCase;

final class RoleCreateParamsUnitTest extends TestCase
{
    public function test(): void
    {
        $value = new RoleCreateParams();
        $nameOneValue = 'one';
        $nameOtherValue = 'other';
        $copy = $value->name($nameOneValue);
        $this->assertSame($value, $copy);
        $this->assertEquals('one', $value->getName());
        $this->assertNotEquals('other', $value->getName());
        $this->assertEquals('one', $value->getNameOrDefault(RoleNameVO::from($nameOtherValue))->value());
        $this->assertNotEquals('other', $value->getNameOrDefault(RoleNameVO::from($nameOtherValue))->value());
        $value->unsetName();
        $this->assertEquals('other', $value->getNameOrDefault(RoleNameVO::from($nameOtherValue))->value());
        $this->assertNotEquals('one', $value->getNameOrDefault(RoleNameVO::from($nameOtherValue))->value());
        $tenantOneValue = new TenantRef('one');
        $tenantOtherValue = new TenantRef('other');
        $copy = $value->tenant($tenantOneValue);
        $this->assertSame($value, $copy);
        $this->assertEquals(new TenantRef('one'), $value->getTenant());
        $this->assertNotEquals(new TenantRef('other'), $value->getTenant());
        $this->assertEquals(new TenantRef('one'), $value->getTenantOrDefault(RoleTenantVO::from($tenantOtherValue))->value());
        $this->assertNotEquals(new TenantRef('other'), $value->getTenantOrDefault(RoleTenantVO::from($tenantOtherValue))->value());
        $value->unsetTenant();
        $this->assertEquals(new TenantRef('other'), $value->getTenantOrDefault(RoleTenantVO::from($tenantOtherValue))->value());
        $this->assertNotEquals(new TenantRef('one'), $value->getTenantOrDefault(RoleTenantVO::from($tenantOtherValue))->value());
        $versionOneValue = 1;
        $versionOtherValue = 2;
        $copy = $value->version($versionOneValue);
        $this->assertSame($value, $copy);
        $this->assertEquals(1, $value->getVersion());
        $this->assertNotEquals(2, $value->getVersion());
        $this->assertEquals(1, $value->getVersionOrDefault(RoleVersionVO::from($versionOtherValue))->value());
        $this->assertNotEquals(2, $value->getVersionOrDefault(RoleVersionVO::from($versionOtherValue))->value());
        $value->unsetVersion();
        $this->assertEquals(2, $value->getVersionOrDefault(RoleVersionVO::from($versionOtherValue))->value());
        $this->assertNotEquals(1, $value->getVersionOrDefault(RoleVersionVO::from($versionOtherValue))->value());
        $this->assertSame($value, $copy);
    }
}
