<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsSecurityDomainVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;

final class RoleDomainsSecurityDomainVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock();
        $ref = RoleDomainsSecurityDomainVO::from($value);
        $this->assertEquals($this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock(), $ref->value());
        $other = RoleDomainsSecurityDomainVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = RoleDomainsSecurityDomainVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = RoleDomainsSecurityDomainVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
