<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsVO;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsItem;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsUidVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsListRef;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsSecurityDomainVO;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsVersionVO;

final class RoleDomainsVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new RoleDomainsListRef(new RoleDomainsItem(
            RoleDomainsUidVO::from('one'),
            RoleDomainsSecurityDomainVO::from($this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock()),
            RoleDomainsVersionVO::from(1)
        ));
        $ref = RoleDomainsVO::from($value);
        $this->assertEquals(new RoleDomainsListRef(new RoleDomainsItem(
            RoleDomainsUidVO::from('one'),
            RoleDomainsSecurityDomainVO::from($this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock()),
            RoleDomainsVersionVO::from(1)
        )), $ref->value());
        $this->assertTrue(!!$ref->find('one'));
        $this->assertTrue(!$ref->find('other'));
        $current = new RoleDomainsItem(
            RoleDomainsUidVO::from('one'),
            null,
            null,
        );
        $cloned = RoleDomainsVO::from([$current]);
        $clonedFilled = $cloned->getWithDefaults($ref);
        $clonedValue = $cloned->value();
        $clonedFilledValue = $clonedFilled->value();
        $this->assertNull($clonedValue->current()->getSecurityDomain());
        $this->assertEquals($this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock(), $clonedFilledValue->current()->getSecurityDomain());
        $this->assertNull($clonedValue->current()->getVersion());
        $this->assertEquals(1, $clonedFilledValue->current()->getVersion());
        $other = RoleDomainsVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = RoleDomainsVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = RoleDomainsVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_build_empty(): void
    {
        $errors = new ConstraintFailList();
        $temp = RoleDomainsVO::tryFrom([], $errors);
        $this->assertFalse($temp->value()->valid());
        $temp = RoleDomainsVO::tryFrom(null, $errors);
        $this->assertNull($temp->value());
    }
}
