<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\ScopeAssignationSecurityScopeVO;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;

final class ScopeAssignationSecurityScopeVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = $this->getMockBuilder(SecurityScopeRef::class)->setConstructorArgs(['one'])->getMock();
        $ref = ScopeAssignationSecurityScopeVO::from($value);
        $this->assertEquals($this->getMockBuilder(SecurityScopeRef::class)->setConstructorArgs(['one'])->getMock(), $ref->value());
        $other = ScopeAssignationSecurityScopeVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = ScopeAssignationSecurityScopeVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = ScopeAssignationSecurityScopeVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
