<?php

# @autogenerated
declare(strict_types=1);

use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;
use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ScopeAttributePermission;

final class ScopeAttributePermissionUnitTest extends TestCase
{
    public function test_init_store_values(): void
    {
        // @Arrange
        $one = new ScopeAttributePermission(
            uid: 'one',
            securityDomain: $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock(),
            securityAttribute: $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['one'])->getMock(),
            permision: 'VIEW',
            version: 1,
        );

        // @Act
        $other = $one->toAttributes()->build();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getSecurityDomain(), $other->getSecurityDomain());
        $this->assertTrue($one->isSecurityDomainChanged());
        $this->assertEquals($one->getSecurityAttribute(), $other->getSecurityAttribute());
        $this->assertTrue($one->isSecurityAttributeChanged());
        $this->assertEquals($one->getPermision(), $other->getPermision());
        $this->assertTrue($one->isPermisionChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_replace_change_values(): void
    {
        // @Arrange
        $base = new ScopeAttributePermission(
            uid: 'one',
            securityDomain: $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock(),
            securityAttribute: $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['one'])->getMock(),
            permision: 'VIEW',
            version: 1,
        );
        $other = new ScopeAttributePermission(
            uid: 'other',
            securityDomain: $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['other'])->getMock(),
            securityAttribute: $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['other'])->getMock(),
            permision: 'MODIFY',
            version: 2,
        );

        // @Act
        $one = $base->replace($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getSecurityDomain(), $other->getSecurityDomain());
        $this->assertTrue($one->isSecurityDomainChanged($base));
        $this->assertEquals($one->getSecurityAttribute(), $other->getSecurityAttribute());
        $this->assertTrue($one->isSecurityAttributeChanged($base));
        $this->assertEquals($one->getPermision(), $other->getPermision());
        $this->assertTrue($one->isPermisionChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
    }
    public function test_create_store_values(): void
    {
        // @Arrange
        $one = new ScopeAttributePermission(
            uid: 'one',
            securityDomain: $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock(),
            securityAttribute: $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['one'])->getMock(),
            permision: 'VIEW',
            version: 1,
        );

        // @Act
        $other = ScopeAttributePermission::create($one->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getSecurityDomain(), $other->getSecurityDomain());
        $this->assertTrue($one->isSecurityDomainChanged());
        $this->assertEquals($one->getSecurityAttribute(), $other->getSecurityAttribute());
        $this->assertTrue($one->isSecurityAttributeChanged());
        $this->assertEquals($one->getPermision(), $other->getPermision());
        $this->assertTrue($one->isPermisionChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
        $this->assertCount(0, $one->getTheEvents());
        $this->assertCount(1, $other->getTheEvents());
    }
    public function test_update_change_values(): void
    {
        $base = new ScopeAttributePermission(
            uid: 'one',
            securityDomain: $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock(),
            securityAttribute: $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['one'])->getMock(),
            permision: 'VIEW',
            version: 1,
        );
        $other = new ScopeAttributePermission(
            uid: 'other',
            securityDomain: $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['other'])->getMock(),
            securityAttribute: $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['other'])->getMock(),
            permision: 'MODIFY',
            version: 2,
        );

        // @Act
        $one = $base->update($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getSecurityDomain(), $other->getSecurityDomain());
        $this->assertTrue($one->isSecurityDomainChanged($base));
        $this->assertEquals($one->getSecurityAttribute(), $other->getSecurityAttribute());
        $this->assertTrue($one->isSecurityAttributeChanged($base));
        $this->assertEquals($one->getPermision(), $other->getPermision());
        $this->assertTrue($one->isPermisionChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
        $this->assertCount(0, $other->getTheEvents());
        $this->assertCount(1, $one->getTheEvents());
    }
    public function test_delete_store_values(): void
    {
        // @Arrange
        $one = new ScopeAttributePermission(
            uid: 'one',
            securityDomain: $this->getMockBuilder(SecurityDomainRef::class)->setConstructorArgs(['one'])->getMock(),
            securityAttribute: $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['one'])->getMock(),
            permision: 'VIEW',
            version: 1,
        );

        // @Act
        $other = $one->delete();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getSecurityDomain(), $other->getSecurityDomain());
        $this->assertTrue($one->isSecurityDomainChanged());
        $this->assertEquals($one->getSecurityAttribute(), $other->getSecurityAttribute());
        $this->assertTrue($one->isSecurityAttributeChanged());
        $this->assertEquals($one->getPermision(), $other->getPermision());
        $this->assertTrue($one->isPermisionChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
}
