<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ValueObject\ScopeAttributePermissionPermisionVO;

final class ScopeAttributePermissionPermisionVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 'VIEW';
        $ref = ScopeAttributePermissionPermisionVO::from($value);
        $this->assertEquals('VIEW', $ref->value());
        $other = ScopeAttributePermissionPermisionVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = ScopeAttributePermissionPermisionVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = ScopeAttributePermissionPermisionVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
