<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ValueObject\ScopeAttributePermissionReadVO;

final class ScopeAttributePermissionReadVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = true;
        $ref = ScopeAttributePermissionReadVO::from($value);
        $this->assertEquals(true, $ref->value());
        $other = ScopeAttributePermissionReadVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = ScopeAttributePermissionReadVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = ScopeAttributePermissionReadVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
