<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ValueObject\ScopeAttributePermissionSecurityAttributeVO;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\SecurityAttributeRef;

final class ScopeAttributePermissionSecurityAttributeVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = $this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['one'])->getMock();
        $ref = ScopeAttributePermissionSecurityAttributeVO::from($value);
        $this->assertEquals($this->getMockBuilder(SecurityAttributeRef::class)->setConstructorArgs(['one'])->getMock(), $ref->value());
        $other = ScopeAttributePermissionSecurityAttributeVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = ScopeAttributePermissionSecurityAttributeVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = ScopeAttributePermissionSecurityAttributeVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
