<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ValueObject\ScopeAttributePermissionWriteVO;

final class ScopeAttributePermissionWriteVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = true;
        $ref = ScopeAttributePermissionWriteVO::from($value);
        $this->assertEquals(true, $ref->value());
        $other = ScopeAttributePermissionWriteVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = ScopeAttributePermissionWriteVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = ScopeAttributePermissionWriteVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
