<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityAttribute\Domain\ValueObject\SecurityAttributeWriteVisibilityVO;

final class SecurityAttributeWriteVisibilityVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 'PUBLIC';
        $ref = SecurityAttributeWriteVisibilityVO::from($value);
        $this->assertEquals('PUBLIC', $ref->value());
        $other = SecurityAttributeWriteVisibilityVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = SecurityAttributeWriteVisibilityVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = SecurityAttributeWriteVisibilityVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = SecurityAttributeWriteVisibilityVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = SecurityAttributeWriteVisibilityVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
