<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomain;

final class SecurityDomainUnitTest extends TestCase
{
    public function test_init_store_values(): void
    {
        // @Arrange
        $one = new SecurityDomain(
            uid: 'one',
            name: 'one',
            level: 1,
            readAll: true,
            writeAll: true,
            manageAll: true,
            viewAllAttributes: true,
            modificaAllAttributes: true,
            enabled: true,
            version: 1,
        );

        // @Act
        $other = $one->toAttributes()->build();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getName(), $other->getName());
        $this->assertTrue($one->isNameChanged());
        $this->assertEquals($one->getLevel(), $other->getLevel());
        $this->assertTrue($one->isLevelChanged());
        $this->assertEquals($one->getReadAll(), $other->getReadAll());
        $this->assertTrue($one->isReadAllChanged());
        $this->assertEquals($one->getWriteAll(), $other->getWriteAll());
        $this->assertTrue($one->isWriteAllChanged());
        $this->assertEquals($one->getManageAll(), $other->getManageAll());
        $this->assertTrue($one->isManageAllChanged());
        $this->assertEquals($one->getViewAllAttributes(), $other->getViewAllAttributes());
        $this->assertTrue($one->isViewAllAttributesChanged());
        $this->assertEquals($one->getModificaAllAttributes(), $other->getModificaAllAttributes());
        $this->assertTrue($one->isModificaAllAttributesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_replace_change_values(): void
    {
        // @Arrange
        $base = new SecurityDomain(
            uid: 'one',
            name: 'one',
            level: 1,
            readAll: true,
            writeAll: true,
            manageAll: true,
            viewAllAttributes: true,
            modificaAllAttributes: true,
            enabled: true,
            version: 1,
        );
        $other = new SecurityDomain(
            uid: 'other',
            name: 'other',
            level: 2,
            readAll: false,
            writeAll: false,
            manageAll: false,
            viewAllAttributes: false,
            modificaAllAttributes: false,
            enabled: false,
            version: 2,
        );

        // @Act
        $one = $base->replace($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getName(), $other->getName());
        $this->assertTrue($one->isNameChanged($base));
        $this->assertEquals($one->getLevel(), $other->getLevel());
        $this->assertTrue($one->isLevelChanged($base));
        $this->assertEquals($one->getReadAll(), $other->getReadAll());
        $this->assertTrue($one->isReadAllChanged($base));
        $this->assertEquals($one->getWriteAll(), $other->getWriteAll());
        $this->assertTrue($one->isWriteAllChanged($base));
        $this->assertEquals($one->getManageAll(), $other->getManageAll());
        $this->assertTrue($one->isManageAllChanged($base));
        $this->assertEquals($one->getViewAllAttributes(), $other->getViewAllAttributes());
        $this->assertTrue($one->isViewAllAttributesChanged($base));
        $this->assertEquals($one->getModificaAllAttributes(), $other->getModificaAllAttributes());
        $this->assertTrue($one->isModificaAllAttributesChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
    }
    public function test_create_store_values(): void
    {
        // @Arrange
        $one = new SecurityDomain(
            uid: 'one',
            name: 'one',
            level: 1,
            readAll: true,
            writeAll: true,
            manageAll: true,
            viewAllAttributes: true,
            modificaAllAttributes: true,
            enabled: true,
            version: 1,
        );

        // @Act
        $other = SecurityDomain::create($one->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getName(), $other->getName());
        $this->assertTrue($one->isNameChanged());
        $this->assertEquals($one->getLevel(), $other->getLevel());
        $this->assertTrue($one->isLevelChanged());
        $this->assertEquals($one->getReadAll(), $other->getReadAll());
        $this->assertTrue($one->isReadAllChanged());
        $this->assertEquals($one->getWriteAll(), $other->getWriteAll());
        $this->assertTrue($one->isWriteAllChanged());
        $this->assertEquals($one->getManageAll(), $other->getManageAll());
        $this->assertTrue($one->isManageAllChanged());
        $this->assertEquals($one->getViewAllAttributes(), $other->getViewAllAttributes());
        $this->assertTrue($one->isViewAllAttributesChanged());
        $this->assertEquals($one->getModificaAllAttributes(), $other->getModificaAllAttributes());
        $this->assertTrue($one->isModificaAllAttributesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
