    public function test_enable_modify(): void
    {
        // @Arrange
        $source = new SecurityDomain(
            uid: 'one',
            name: 'one',
            level: 1,
            readAll: true,
            writeAll: true,
            manageAll: true,
            viewAllAttributes: true,
            modificaAllAttributes: true,
            enabled: true,
            version: 1,
        );
        $sourceEnabled = false;
        $targetEnabled = true;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->enable();

        // @Assert
        $this->assertEquals(false, $source->getEnabled());
        $this->assertEquals($targetEnabled, $target->getEnabled());
        $this->assertNotEquals($sourceEnabled, $target->getEnabled());
    }
    public function test_disable_modify(): void
    {
        // @Arrange
        $source = new SecurityDomain(
            uid: 'one',
            name: 'one',
            level: 1,
            readAll: true,
            writeAll: true,
            manageAll: true,
            viewAllAttributes: true,
            modificaAllAttributes: true,
            enabled: true,
            version: 1,
        );
        $sourceEnabled = true;
        $targetEnabled = false;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->disable();

        // @Assert
        $this->assertEquals(true, $source->getEnabled());
        $this->assertEquals($targetEnabled, $target->getEnabled());
        $this->assertNotEquals($sourceEnabled, $target->getEnabled());
    }
}
