<?php

# @autogenerated
declare(strict_types=1);

use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeAttributes;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScope;

final class SecurityScopeUnitTest extends TestCase
{
    public function test_init_store_values(): void
    {
        // @Arrange
        $one = new SecurityScope(
            uid: 'one',
            trustedClient: $this->getMockBuilder(TrustedClientRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            resource: 'one',
            scope: 'one',
            enabled: true,
            kind: 'MANAGE',
            visibility: 'EXPLICIT',
            version: 1,
        );

        // @Act
        $other = $one->toAttributes()->build();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getTrustedClient(), $other->getTrustedClient());
        $this->assertTrue($one->isTrustedClientChanged());
        $this->assertEquals($one->getRelyingParty(), $other->getRelyingParty());
        $this->assertTrue($one->isRelyingPartyChanged());
        $this->assertEquals($one->getResource(), $other->getResource());
        $this->assertTrue($one->isResourceChanged());
        $this->assertEquals($one->getScope(), $other->getScope());
        $this->assertTrue($one->isScopeChanged());
        $this->assertEquals($one->getKind(), $other->getKind());
        $this->assertTrue($one->isKindChanged());
        $this->assertEquals($one->getVisibility(), $other->getVisibility());
        $this->assertTrue($one->isVisibilityChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_replace_change_values(): void
    {
        // @Arrange
        $base = new SecurityScope(
            uid: 'one',
            trustedClient: $this->getMockBuilder(TrustedClientRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            resource: 'one',
            scope: 'one',
            enabled: true,
            kind: 'MANAGE',
            visibility: 'EXPLICIT',
            version: 1,
        );
        $other = new SecurityScope(
            uid: 'other',
            trustedClient: $this->getMockBuilder(TrustedClientRef::class)->setConstructorArgs(['other'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['other'])->getMock(),
            resource: 'other',
            scope: 'other',
            enabled: false,
            kind: 'WRITE',
            visibility: 'AUTHORIZED',
            version: 2,
        );

        // @Act
        $one = $base->replace($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getTrustedClient(), $other->getTrustedClient());
        $this->assertTrue($one->isTrustedClientChanged($base));
        $this->assertEquals($one->getRelyingParty(), $other->getRelyingParty());
        $this->assertTrue($one->isRelyingPartyChanged($base));
        $this->assertEquals($one->getResource(), $other->getResource());
        $this->assertTrue($one->isResourceChanged($base));
        $this->assertEquals($one->getScope(), $other->getScope());
        $this->assertTrue($one->isScopeChanged($base));
        $this->assertEquals($one->getKind(), $other->getKind());
        $this->assertTrue($one->isKindChanged($base));
        $this->assertEquals($one->getVisibility(), $other->getVisibility());
        $this->assertTrue($one->isVisibilityChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
    }
