    public function test_disable_modify(): void
    {
        // @Arrange
        $source = new SecurityScope(
            uid: 'one',
            trustedClient: $this->getMockBuilder(TrustedClientRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            resource: 'one',
            scope: 'one',
            enabled: true,
            kind: 'MANAGE',
            visibility: 'EXPLICIT',
            version: 1,
        );
        $sourceEnabled = true;
        $targetEnabled = false;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->disable();

        // @Assert
        $this->assertEquals(true, $source->getEnabled());
        $this->assertEquals($targetEnabled, $target->getEnabled());
        $this->assertNotEquals($sourceEnabled, $target->getEnabled());
    }
    public function test_enable_modify(): void
    {
        // @Arrange
        $source = new SecurityScope(
            uid: 'one',
            trustedClient: $this->getMockBuilder(TrustedClientRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            resource: 'one',
            scope: 'one',
            enabled: true,
            kind: 'MANAGE',
            visibility: 'EXPLICIT',
            version: 1,
        );
        $sourceEnabled = false;
        $targetEnabled = true;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->enable();

        // @Assert
        $this->assertEquals(false, $source->getEnabled());
        $this->assertEquals($targetEnabled, $target->getEnabled());
        $this->assertNotEquals($sourceEnabled, $target->getEnabled());
    }
