<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeKindVO;

final class SecurityScopeKindVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 'READ';
        $ref = SecurityScopeKindVO::from($value);
        $this->assertEquals('READ', $ref->value());
        $other = SecurityScopeKindVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = SecurityScopeKindVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = SecurityScopeKindVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = SecurityScopeKindVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = SecurityScopeKindVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
