<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeRelyingPartyVO;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

final class SecurityScopeRelyingPartyVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock();
        $ref = SecurityScopeRelyingPartyVO::from($value);
        $this->assertEquals($this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(), $ref->value());
        $other = SecurityScopeRelyingPartyVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = SecurityScopeRelyingPartyVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = SecurityScopeRelyingPartyVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = SecurityScopeRelyingPartyVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = SecurityScopeRelyingPartyVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
