<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeTrustedClientVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;

final class SecurityScopeTrustedClientVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = $this->getMockBuilder(TrustedClientRef::class)->setConstructorArgs(['one'])->getMock();
        $ref = SecurityScopeTrustedClientVO::from($value);
        $this->assertEquals($this->getMockBuilder(TrustedClientRef::class)->setConstructorArgs(['one'])->getMock(), $ref->value());
        $other = SecurityScopeTrustedClientVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = SecurityScopeTrustedClientVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = SecurityScopeTrustedClientVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = SecurityScopeTrustedClientVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = SecurityScopeTrustedClientVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
