<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeVersionVO;

final class SecurityScopeVersionVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 1;
        $ref = SecurityScopeVersionVO::from($value);
        $this->assertEquals(1, $ref->value());
        $other = SecurityScopeVersionVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = SecurityScopeVersionVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = SecurityScopeVersionVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = SecurityScopeVersionVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = SecurityScopeVersionVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
