<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\ValueObject\SecurityScopeVisibilityVO;

final class SecurityScopeVisibilityVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 'EXPLICIT';
        $ref = SecurityScopeVisibilityVO::from($value);
        $this->assertEquals('EXPLICIT', $ref->value());
        $other = SecurityScopeVisibilityVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = SecurityScopeVisibilityVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = SecurityScopeVisibilityVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = SecurityScopeVisibilityVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = SecurityScopeVisibilityVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
