<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\ValueObject\TenantConfigRecoverPassEmailVO;

final class TenantConfigRecoverPassEmailVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 'one';
        $ref = TenantConfigRecoverPassEmailVO::from($value);
        $this->assertEquals('one', $ref->value());
        $other = TenantConfigRecoverPassEmailVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = TenantConfigRecoverPassEmailVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = TenantConfigRecoverPassEmailVO::tryFrom(1, $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = TenantConfigRecoverPassEmailVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = TenantConfigRecoverPassEmailVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
