<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\TenantConfig\Domain\ValueObject\TenantConfigTenantVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;

final class TenantConfigTenantVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock();
        $ref = TenantConfigTenantVO::from($value);
        $this->assertEquals($this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(), $ref->value());
        $other = TenantConfigTenantVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = TenantConfigTenantVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = TenantConfigTenantVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
