    public function test_enable_modify(): void
    {
        // @Arrange
        $source = new TenantLoginProvider(
            uid: 'one',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(),
            name: 'one',
            source: TenantLoginProviderSourceOptions::GOOGLE,
            disabled: true,
            directAccess: true,
            publicKey: 'one',
            privateKey: 'one',
            certificate: 'one',
            metadata: 'store://bin',
            usersEnabledByDefault: true,
            version: 1,
        );
        $sourceDisabled = true;
        $targetDisabled = false;
        $source = $source->withDisabled($sourceDisabled);

        // @Act
        $target = $source->enable();

        // @Assert
        $this->assertEquals(true, $source->getDisabled());
        $this->assertEquals($targetDisabled, $target->getDisabled());
        $this->assertNotEquals($sourceDisabled, $target->getDisabled());
    }
    public function test_disable_modify(): void
    {
        // @Arrange
        $source = new TenantLoginProvider(
            uid: 'one',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(),
            name: 'one',
            source: TenantLoginProviderSourceOptions::GOOGLE,
            disabled: true,
            directAccess: true,
            publicKey: 'one',
            privateKey: 'one',
            certificate: 'one',
            metadata: 'store://bin',
            usersEnabledByDefault: true,
            version: 1,
        );
        $sourceDisabled = false;
        $targetDisabled = true;
        $source = $source->withDisabled($sourceDisabled);

        // @Act
        $target = $source->disable();

        // @Assert
        $this->assertEquals(false, $source->getDisabled());
        $this->assertEquals($targetDisabled, $target->getDisabled());
        $this->assertNotEquals($sourceDisabled, $target->getDisabled());
    }
