<?php

# @autogenerated
declare(strict_types=1);

use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurity;

final class TenantPartySecurityUnitTest extends TestCase
{
    public function test_init_store_values(): void
    {
        // @Arrange
        $one = new TenantPartySecurity(
            uid: 'one',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            policies: 'one',
            version: 1,
        );

        // @Act
        $other = $one->toAttributes()->build();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getTenant(), $other->getTenant());
        $this->assertTrue($one->isTenantChanged());
        $this->assertEquals($one->getRelyingParty(), $other->getRelyingParty());
        $this->assertTrue($one->isRelyingPartyChanged());
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_replace_change_values(): void
    {
        // @Arrange
        $base = new TenantPartySecurity(
            uid: 'one',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            policies: 'one',
            version: 1,
        );
        $other = new TenantPartySecurity(
            uid: 'other',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['other'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['other'])->getMock(),
            policies: 'other',
            version: 2,
        );

        // @Act
        $one = $base->replace($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getTenant(), $other->getTenant());
        $this->assertTrue($one->isTenantChanged($base));
        $this->assertEquals($one->getRelyingParty(), $other->getRelyingParty());
        $this->assertTrue($one->isRelyingPartyChanged($base));
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
    }
    public function test_create_store_values(): void
    {
        // @Arrange
        $one = new TenantPartySecurity(
            uid: 'one',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            policies: 'one',
            version: 1,
        );

        // @Act
        $other = TenantPartySecurity::create($one->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getTenant(), $other->getTenant());
        $this->assertTrue($one->isTenantChanged());
        $this->assertEquals($one->getRelyingParty(), $other->getRelyingParty());
        $this->assertTrue($one->isRelyingPartyChanged());
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
        $this->assertCount(0, $one->getTheEvents());
        $this->assertCount(1, $other->getTheEvents());
    }
    public function test_update_change_values(): void
    {
        $base = new TenantPartySecurity(
            uid: 'one',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            policies: 'one',
            version: 1,
        );
        $other = new TenantPartySecurity(
            uid: 'other',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['other'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['other'])->getMock(),
            policies: 'other',
            version: 2,
        );

        // @Act
        $one = $base->update($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getTenant(), $other->getTenant());
        $this->assertTrue($one->isTenantChanged($base));
        $this->assertEquals($one->getRelyingParty(), $other->getRelyingParty());
        $this->assertTrue($one->isRelyingPartyChanged($base));
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
        $this->assertCount(0, $other->getTheEvents());
        $this->assertCount(1, $one->getTheEvents());
    }
    public function test_delete_store_values(): void
    {
        // @Arrange
        $one = new TenantPartySecurity(
            uid: 'one',
            tenant: $this->getMockBuilder(TenantRef::class)->setConstructorArgs(['one'])->getMock(),
            relyingParty: $this->getMockBuilder(RelyingPartyRef::class)->setConstructorArgs(['one'])->getMock(),
            policies: 'one',
            version: 1,
        );

        // @Act
        $other = $one->delete();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getTenant(), $other->getTenant());
        $this->assertTrue($one->isTenantChanged());
        $this->assertEquals($one->getRelyingParty(), $other->getRelyingParty());
        $this->assertTrue($one->isRelyingPartyChanged());
        $this->assertEquals($one->getPolicies(), $other->getPolicies());
        $this->assertTrue($one->isPoliciesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
}
