<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityVersionVO;

final class TenantPartySecurityVersionVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 1;
        $ref = TenantPartySecurityVersionVO::from($value);
        $this->assertEquals(1, $ref->value());
        $other = TenantPartySecurityVersionVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = TenantPartySecurityVersionVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = TenantPartySecurityVersionVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = TenantPartySecurityVersionVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = TenantPartySecurityVersionVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
