<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseTenantVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;

final class TenantTermsOfUseTenantVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new TenantRef('one');
        $ref = TenantTermsOfUseTenantVO::from($value);
        $this->assertEquals(new TenantRef('one'), $ref->value());
        $other = TenantTermsOfUseTenantVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = TenantTermsOfUseTenantVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = TenantTermsOfUseTenantVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
