<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserApproveVO;
use Civi\Lughauth\Features\Access\User\Domain\UserApproveOptions;

final class UserApproveVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = UserApproveOptions::PENDING;
        $ref = UserApproveVO::from($value);
        $this->assertEquals(UserApproveOptions::PENDING, $ref->value());
        $other = UserApproveVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = UserApproveVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = UserApproveVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = UserApproveVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = UserApproveVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
