<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserBlockedUntilVO;

final class UserBlockedUntilVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = (new \DateTimeImmutable('1980-08-20T14:32:45.123Z'));
        $ref = UserBlockedUntilVO::from($value);
        $this->assertEquals((new \DateTimeImmutable('1980-08-20T14:32:45.123Z')), $ref->value());
        $other = UserBlockedUntilVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = UserBlockedUntilVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = UserBlockedUntilVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
        $errors = new ConstraintFailList();
        $other = UserBlockedUntilVO::tryFrom(22, $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = UserBlockedUntilVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = UserBlockedUntilVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
    public function test_read_date_format(): void
    {
        $errors = new ConstraintFailList();
        $value = '2024-05-26T14:32:45.123Z';
        $other = UserBlockedUntilVO::tryFrom($value, $errors);
        $date = new \DateTimeImmutable($value);
        $this->assertEquals($date, $other->value());
        $this->assertTrue($errors->isEmpty());
        $invalid = '2024-15-26T14:32:45.123Z';
        $other = UserBlockedUntilVO::tryFrom($invalid, $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
