<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserFailedLoginAttemptsVO;

final class UserFailedLoginAttemptsVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = 1;
        $ref = UserFailedLoginAttemptsVO::from($value);
        $this->assertEquals(1, $ref->value());
        $other = UserFailedLoginAttemptsVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = UserFailedLoginAttemptsVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = UserFailedLoginAttemptsVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = UserFailedLoginAttemptsVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = UserFailedLoginAttemptsVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
