<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\UserAccessTemporalCode\Domain\ValueObject\UserAccessTemporalCodeUserVO;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;

final class UserAccessTemporalCodeUserVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = $this->getMockBuilder(UserRef::class)->setConstructorArgs(['one'])->getMock();
        $ref = UserAccessTemporalCodeUserVO::from($value);
        $this->assertEquals($this->getMockBuilder(UserRef::class)->setConstructorArgs(['one'])->getMock(), $ref->value());
        $other = UserAccessTemporalCodeUserVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = UserAccessTemporalCodeUserVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = UserAccessTemporalCodeUserVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
