<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesVO;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesItem;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesUidVO;
use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesListRef;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesRoleVO;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesVersionVO;

final class UserIdentityRolesVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new UserIdentityRolesListRef(new UserIdentityRolesItem(
            UserIdentityRolesUidVO::from('one'),
            UserIdentityRolesRoleVO::from(new RoleRef('one')),
            UserIdentityRolesVersionVO::from(1)
        ));
        $ref = UserIdentityRolesVO::from($value);
        $this->assertEquals(new UserIdentityRolesListRef(new UserIdentityRolesItem(
            UserIdentityRolesUidVO::from('one'),
            UserIdentityRolesRoleVO::from(new RoleRef('one')),
            UserIdentityRolesVersionVO::from(1)
        )), $ref->value());
        $this->assertTrue(!!$ref->find('one'));
        $this->assertTrue(!$ref->find('other'));
        $current = new UserIdentityRolesItem(
            UserIdentityRolesUidVO::from('one'),
            null,
            null,
        );
        $cloned = UserIdentityRolesVO::from([$current]);
        $clonedFilled = $cloned->getWithDefaults($ref);
        $clonedValue = $cloned->value();
        $clonedFilledValue = $clonedFilled->value();
        $this->assertNull($clonedValue->current()->getRole());
        $this->assertEquals(new RoleRef('one'), $clonedFilledValue->current()->getRole());
        $this->assertNull($clonedValue->current()->getVersion());
        $this->assertEquals(1, $clonedFilledValue->current()->getVersion());
        $other = UserIdentityRolesVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = UserIdentityRolesVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = UserIdentityRolesVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_build_empty(): void
    {
        $errors = new ConstraintFailList();
        $temp = UserIdentityRolesVO::tryFrom([], $errors);
        $this->assertFalse($temp->value()->valid());
        $temp = UserIdentityRolesVO::tryFrom(null, $errors);
        $this->assertNull($temp->value());
    }
}
