<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityUserVO;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;

final class UserIdentityUserVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new UserRef('one');
        $ref = UserIdentityUserVO::from($value);
        $this->assertEquals(new UserRef('one'), $ref->value());
        $other = UserIdentityUserVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = UserIdentityUserVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = UserIdentityUserVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
