<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Infrastructure\Audit\AuditChange;

final class AuditChangeUnitTest extends TestCase
{
    public function testPropertiesStored(): void
    {
        $change = new AuditChange('action-1', 'User', '1', 'insert', ['name' => 'A'], ['ip' => '127.0.0.1']);

        $this->assertSame('action-1', $change->actionId);
        $this->assertSame('User', $change->targetType);
        $this->assertSame('1', $change->targetId);
        $this->assertSame('insert', $change->changeType);
        $this->assertSame(['name' => 'A'], $change->payload);
        $this->assertSame(['ip' => '127.0.0.1'], $change->metadata);
    }
}
