<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskContext;

/**
 * Unit tests for {@see TaskContext}.
 */
final class TaskContextUnitTest extends TestCase
{
    /**
     * Ensures task context exposes parameters and cursor.
     */
    public function testContextStoresParamsAndCursor(): void
    {
        /* Arrange: prepare task parameters and a cursor value. */
        $params = ['id' => 10];
        $cursor = 'next';

        /* Act: instantiate the task context. */
        $context = new TaskContext($params, $cursor);

        /* Assert: verify the context retains the provided values. */
        $this->assertSame($params, $context->params);
        $this->assertSame($cursor, $context->cursor);
    }
}
