<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;

/**
 * Unit tests for {@see TaskKey}.
 */
final class TaskKeyUnitTest extends TestCase
{
    /**
     * Ensures the task key holds the provided identifier.
     */
    public function testKeyStoresUid(): void
    {
        /* Arrange: define a task identifier. */
        $uid = 'task-1';

        /* Act: create the task key. */
        $key = new TaskKey($uid);

        /* Assert: verify the key stores the identifier. */
        $this->assertSame($uid, $key->uid);
    }
}
