<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Infrastructure\MicroConfig;

final class MicroConfigUnitTest extends TestCase
{
    public function testDefaultsEnableAllFeatures(): void
    {
        $config = new MicroConfig();

        $this->assertTrue($config->withRate);
        $this->assertTrue($config->withMetrics);
        $this->assertTrue($config->withTelemetry);
        $this->assertTrue($config->withManagement);
        $this->assertTrue($config->withAudit);
    }
}
