<?php

/* @autogenerated */
declare(strict_types=1);

namespace {
    use PHPUnit\Framework\TestCase;
    use Civi\Lughauth\Shared\Infrastructure\Sql\SqlParam;

    /**
     * Unit tests for {@see SqlParam}.
     */
    final class SqlParamTest extends TestCase
    {
        /**
         * Ensures constructor assigns provided values.
         */
        public function testConstructorAssignsValues(): void
        {
            /*
             * Arrange: build a SQL parameter payload.
             */
            $name = 'id';
            $value = 10;
            $type = SqlParam::INT;

            /*
             * Act: construct the SqlParam instance.
             */
            $param = new SqlParam($name, $value, $type);

            /*
             * Assert: verify the public properties are set.
             */
            $this->assertSame($name, $param->name);
            $this->assertSame($value, $param->value);
            $this->assertSame($type, $param->type);
        }
    }
}
