<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\FloatVal;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class FloatValUnitTest extends TestCase
{
    public function testValidFloats(): void
    {
        $rule = new FloatVal();
        $this->assertNull($rule->check(3.14));
        $this->assertNull($rule->check(0.0));
        $this->assertNull($rule->check('-5.5'));
        $this->assertNull($rule->check('4.20')); // string convertible to float
    }

    public function testInvalidFloats(): void
    {
        $rule = new FloatVal();
        $invalid = ['abc', '', null, [], new stdClass()];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_float_val', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
