<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\HexColor;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class HexColorUnitTest extends TestCase
{
    public function testValidHexColors(): void
    {
        $rule = new HexColor();
        $valid = ['#FFF', '#ffffff', '#123ABC', '#000000'];

        foreach ($valid as $color) {
            $this->assertNull($rule->check($color));
        }
    }

    public function testInvalidHexColors(): void
    {
        $rule = new HexColor();
        $invalid = ['NOFFF', '#1234', '#GGGGGG', '#123ABZ', '#1234567', ''];

        foreach ($invalid as $color) {
            $result = $rule->check($color);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_hex_color', $result->code);
            $this->assertEquals($color, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
