<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Imei;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class ImeiUnitTest extends TestCase
{
    public function testValidImeiNumbers(): void
    {
        $rule = new Imei();
        $this->assertNull($rule->check('490154203237518')); // IMEI vÃ¡lido
    }

    public function testInvalidImeiNumbers(): void
    {
        $rule = new Imei();
        $invalid = ['490154203237519', '123456', 'imei123', '', '49015420323751A'];

        foreach ($invalid as $imei) {
            $result = $rule->check($imei);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_imei', $result->code);
            $this->assertEquals($imei, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
