<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\In;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class InUnitTest extends TestCase
{
    public function testValueInList(): void
    {
        $rule = new In(['apple', 'banana', 'cherry']);
        $this->assertNull($rule->check('apple'));
        $this->assertNull($rule->check('cherry'));
    }

    public function testValueNotInList(): void
    {
        $rule = new In(['red', 'green', 'blue']);
        $invalidValues = ['yellow', 'black', '', 'Green']; // case-sensitive

        foreach ($invalidValues as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_in', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals(['red', 'green', 'blue'], $result->expectedValues);
        }
    }
}
