<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\IntVal;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class IntValUnitTest extends TestCase
{
    public function testValidIntegers(): void
    {
        $rule = new IntVal();
        $this->assertNull($rule->check(42));
        $this->assertNull($rule->check(-1));
        $this->assertNull($rule->check('123')); // string numÃ©rica
    }

    public function testInvalidIntegers(): void
    {
        $rule = new IntVal();
        $invalid = ['12.3', 'abc', '', 3.14, null];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_int_value', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
