<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Ip;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class IpUnitTest extends TestCase
{
    public function testValidIpAddresses(): void
    {
        $rule = new Ip();
        $this->assertNull($rule->check('192.168.0.1')); // IPv4
        $this->assertNull($rule->check('::1'));         // IPv6 localhost
        $this->assertNull($rule->check('2001:0db8::1'));
    }

    public function testInvalidIpAddresses(): void
    {
        $rule = new Ip();
        $invalid = ['999.999.999.999', '12345::', 'abc.def.ghi.jkl', 'hello world', ''];

        foreach ($invalid as $ip) {
            $result = $rule->check($ip);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_ip', $result->code);
            $this->assertEquals($ip, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
