<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Isbn;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class IsbnUnitTest extends TestCase
{
    public function testValidIsbn(): void
    {
        $rule = new Isbn();
        $this->assertNull($rule->check('1234567890')); // ??
        $this->assertNull($rule->check('0-306-40615-2')); // ISBN-10
        $this->assertNull($rule->check('978-3-16-148410-0')); // ISBN-13
    }

    public function testInvalidIsbn(): void
    {
        $rule = new Isbn();
        $invalid = ['X1234567890', '978-3-16-148410-X', 'abc', '', '9783161484100X'];

        foreach ($invalid as $isbn) {
            $result = $rule->check($isbn);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_isbn', $result->code);
            $this->assertEquals($isbn, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
