<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Json;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class JsonUnitTest extends TestCase
{
    public function testValidJson(): void
    {
        $rule = new Json();
        $this->assertNull($rule->check('{"name": "John"}'));
        $this->assertNull($rule->check('["a", "b", 1, 2]'));
        $this->assertNull($rule->check('123')); // válido como JSON numérico
    }

    public function testInvalidJson(): void
    {
        $rule = new Json();
        $invalid = ['{name: John}', '{"key": "value"', 'abc', '', '{]'];

        foreach ($invalid as $json) {
            $result = $rule->check($json);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_json', $result->code);
            $this->assertEquals($json, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
