<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Length;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class LengthUnitTest extends TestCase
{
    public function testValidLengths(): void
    {
        $rule = new Length(3, 5);
        $this->assertNull($rule->check('abc'));   // 3
        $this->assertNull($rule->check('abcd'));  // 4
        $this->assertNull($rule->check([1, 2, 3])); // array of length 3
    }

    public function testInvalidLengths(): void
    {
        $rule = new Length(2, 4);
        $invalidValues = ['a', 'abcde', [1], [1,2,3,4,5]];

        foreach ($invalidValues as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_length', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals(['min' => 2, 'max' => 4], $result->expectedValues);
        }
    }
}
