<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Lowercase;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class LowercaseUnitTest extends TestCase
{
    public function testValidLowercase(): void
    {
        $rule = new Lowercase();
        $this->assertNull($rule->check('abc'));
        $this->assertNull($rule->check('abc123'));
        $this->assertNull($rule->check('áéíóú'));
        $this->assertNull($rule->check('lowercase'));
        $this->assertNull($rule->check(''));
        $this->assertNull($rule->check('withspaceandlower'));
    }

    public function testInvalidLowercase(): void
    {
        $rule = new Lowercase();
        $invalid = ['Abc', 'LOWER', '123Abc', 'aBc!', 'XX', 'LÁéíóú'];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_lowercase', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
