<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\MacAddress;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class MacAddressUnitTest extends TestCase
{
    public function testValidMacAddresses(): void
    {
        $rule = new MacAddress();
        $this->assertNull($rule->check('00:1A:2B:3C:4D:5E'));
        $this->assertNull($rule->check('00-1A-2B-3C-4D-5E'));
        $this->assertNull($rule->check('aa:bb:cc:dd:ee:ff'));
    }

    public function testInvalidMacAddresses(): void
    {
        $rule = new MacAddress();
        $invalid = ['001A.2B3C.4D5E', '00:1A:2B:3C:4D', 'invalid', '001A2B3C4D5E', '', 'zz:zz:zz:zz:zz:zz'];

        foreach ($invalid as $mac) {
            $result = $rule->check($mac);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_mac_addres', $result->code);  // respetamos el typo original del cÃ³digo
            $this->assertEquals($mac, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
