<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Max;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class MaxUnitTest extends TestCase
{
    public function testValidMaxValues(): void
    {
        $rule = new Max(10);
        $this->assertNull($rule->check(5));
        $this->assertNull($rule->check(10));
        $this->assertNull($rule->check('7')); // string convertible
    }

    public function testInvalidMaxValues(): void
    {
        $rule = new Max(10);
        $invalid = [11, 20, '15', 10.1];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_max', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals(['max' => 10], $result->expectedValues);
        }
    }
}
